//       setFormData({ ...formData, [name]: value });
//     } else {
//       // For other fields, update as usual
//       setFormData({ ...formData, [name]: value });
//     }
//   };

  return (



    <div>
      <form>
        <label>Course Name:
          <input type="text" /></label>
        <br />
        <label>
          Type of test:
        </label>
        <div>
          {typeOfTest.map((type) => (
            <div key={type.typeOfTestId}>
              <input type="checkbox" /> {type.typeOfTestName}
            </div>
          ))}
        </div>
        <br />
        <div>
          <label htmlFor="exams">Select Exam:</label>
          <select id="exams" value={selectedexams} onChange={handleexams}>
            <option value="">Select exams</option>
            {exams.map(exams => (
              <option key={exams.examId} value={exams.examId}>
                {exams.examName}
              </option>
            ))}
          </select>
        </div>
        
         <br />

        <div>
          <label> Select Subjects:</label>
          {selectedSubjects.map(subject => (
            <div key={subject.subjectId}>
              <input
                type="checkbox"
                id={`subject-${subject.subjectId}`}
                name={`subject-${subject.subjectId}`}
                value={subject.subjectId}
                checked={selectedSubjects.includes(subject.subjectId)}
                onChange={(e) => handleSubjectChange(e, subject.subjectId)}
              />
              <label htmlFor={`subject-${subject.subjectId}`}>{subject.subjectName}</label>
            </div>
          ))}
        </div>
        
         <br />

        <label>
          type of Questions:
        </label>
        <div>
          {typeofQuestion.map((type) => (
            <div key={type.quesionTypeId}>
              <input type="checkbox" /> {type.typeofQuestion}
            </div>
          ))}
        </div>
        <br />
        <div>
          <label htmlFor="courseStartDate">Courese Start Date:</label>
          <input
            type="date"
            id="courseStartDate"
            name="courseStartDate"
            value={startDate}
            onChange={handleStartDateChange}
          />
        </div>

        <div>
          <label htmlFor="courseEndDate">Courese End Date:</label>
          <input
            type="date"
            id="courseEndDate"
            name="courseEndDate"
            value={endDate}
            onChange={handleEndDateChange}
          />
        </div> <br />
               <div>
          <label htmlFor="cost">Cost:</label>
          <input
            type="number"
            id="cost"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
          />
        </div>

       <div>
          <label htmlFor="discount">Discount (%):</label>
        <input
            type="number"
            id="discount"
            name="discount"
            value={formData.discount}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="discountAmount">Discount Amount:</label>
          <input
            type="number"
            id="discountAmount"
            name="discountAmount"
            value={formData.discountAmount}
            readOnly // Make it read-only to prevent manual input
          />
        </div>

        <div>
          <label htmlFor="totalPrice">Total Price:</label>
          <input
            type="number"
            id="totalPrice"
            name="totalPrice"
            value={formData.totalPrice}
            readOnly // Make it read-only to prevent manual input
          />
        </div>
      </form>
    </div>
  )
}

