import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './admin.css'
import SuccessPopup from './SuccessPopup';
import EditForm from './EditForm';
function Examcreation() {
    const [examName, setExamName] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [selectedSubjects, setSelectedSubjects] = useState([]);
    const [subjects, setSubjects] = useState([]);
    const [submitting, setSubmitting] = useState(false);
    const [formOpen, setFormOpen] = useState(false);
    const [showSuccessPopup, setShowSuccessPopup] = useState(false);
    const [examsWithSubjects, setExamsWithSubjects] = useState([]);
    // const [isEditing, setIsEditing] = useState(false);
    // const [editedExam, setEditedExam] = useState({});
    // const [editedData, setEditedData] = useState({});
    // const [editedSubjects, setEditedSubjects] = useState([]);
    // const [isEditingSubjects, setIsEditingSubjects] = useState(false);
    // const [selectedExam, setSelectedExam] = useState(null);
    const [showEditForm, setShowEditForm] = useState(false);
    const [selectedExam, setSelectedExam] = useState(null);
    const resetForm = () => {
        setExamName('');
        setStartDate('');
        setEndDate('');
        setSelectedSubjects([]);
    };


    function formatDate(dateString) {
        const date = new Date(dateString);
        const day = date.getDate().toString().padStart(2, '0');
        const month = (date.getMonth() + 1).toString().padStart(2, '0'); // Month is 0-based
        const year = date.getFullYear();
        return `${day}/${month}/${year}`;
      }



    useEffect(() => {
        // Fetch subjects from the backend when the component mounts
        axios.get('http://localhost:3081/subjects')
            .then(response => {
                setSubjects(response.data);
            })
            .catch(error => {
                console.error('Error fetching subjects:', error);
            });
    }, []);

    const handleCheckboxChange = (subjectId) => {
        // Toggle the selection of subjects
        setSelectedSubjects((prevSelected) => {
            if (prevSelected.includes(subjectId)) {
                return prevSelected.filter((id) => id !== subjectId);
            } else {
                return [...prevSelected, subjectId];
            }
        });
    };

//................................... handeler for submit button .............................//
    const handleSubmit = (e) => {
        e.preventDefault();
        setSubmitting(true);

        const examData = {
            examName,
            startDate,
            endDate,
            selectedSubjects,
        };

        axios.post('http://localhost:3081/exams', examData)
            .then(response => {
                console.log('Exam created:', response.data);
                // Reset form fields and state as needed
                setSubmitting(false);
                resetForm();
                window.location.reload();
                setShowSuccessPopup(true);
            })
            .catch(error => {
                console.error('Error creating exam:', error);
                setSubmitting(false);
            });
    };
    const closeSuccessPopup = () => {
        setShowSuccessPopup(false);
    };
    useEffect(() => {
        axios.get('http://localhost:3081/exams-with-subjects')
            .then(response => {
                setExamsWithSubjects(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);
//....................................END...............................//

//.............................Delete button handiler ...................//
    const handleDelete = (examId) => {
        // Handle the "Delete" action for the given examId on the client side
        const confirmDelete = window.confirm('Are you sure you want to delete this data?');
        if (confirmDelete) {
            setExamsWithSubjects(prevExams => prevExams.filter(exam => exam.examId !== examId));

            // Send a request to delete the exam from the server
            axios.delete(`http://localhost:3081/exams/${examId}`)
                .then(response => {
                    console.log(`Exam with ID ${examId} deleted from the database`);
                })
                .catch(error => {
                    console.error('Error deleting exam:', error);
                });
        }
    };
//....................................END...............................//
    
// const handleEdit = (examId) => {
//     // Find the exam to edit based on examId
//     const examToEdit = examsWithSubjects.find((exam) => exam.examId === examId);
//     setSelectedExam(examToEdit);
  
//     // Fetch the subjects for the selected exam
//     axios.get(`http://localhost:3081/exams/${examId}/subjects`)
//       .then((response) => {
//         const selectedSubjects = response.data.map((subject) => subject.subjectName);
//         setEditedSubjects(selectedSubjects);
//       })
//       .catch((error) => {
//         console.error('Error fetching subjects for the selected exam:', error);
//       });
  
//     // Set the form fields in edit mode
//     setEditedData({
//       examName: examToEdit.examName,
//       startDate: examToEdit.startDate,
//       endDate: examToEdit.endDate,
//     });
//   };
  
//   // ...
  
//   // Update exam data (PUT request)
//   const handleUpdate = () => {
//     if (selectedExam) {
//       // Update the edited data on the client side
//       const updatedExam = {
//         examId: selectedExam.examId,
//         examName: editedData.examName,
//         startDate: editedData.startDate,
//         endDate: editedData.endDate,
//         subjects: editedSubjects,
//       };
  
//       setExamsWithSubjects((prevExams) =>
//         prevExams.map((exam) =>
//           exam.examId === selectedExam.examId ? updatedExam : exam
//         )
//       );
//       setSelectedExam(null);
  
//       // Send a request to update the exam data in the database
//       axios.put(`http://localhost:3081/exams-edit/${selectedExam.examId}`, updatedExam)
//         .then((response) => {
//           console.log(`Exam with ID ${selectedExam.examId} updated in the database`);
//         })
//         .catch((error) => {
//           console.error('Error updating exam:', error);
//         });
//     }
//   };
const handleEdit = (examId) => {
    const examToEdit = examsWithSubjects.find((exam) => exam.examId === examId);
    setSelectedExam(examToEdit);
    setShowEditForm(true);
  };
  return (
    <div>
      <h2>Create Exam</h2>
      {formOpen ? (
        <form onSubmit={handleSubmit}>
          <label>
            Exam Name:
            <input type="text" value={examName} onChange={(e) => setExamName(e.target.value)} />
          </label>
          <br />
          <label>
            Start Date:
            <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
          </label>
          <br />
          <label>
            End Date:
            <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
          </label>
          <br />
          <label>Subjects:</label>
          <ul className="subject-list">
            {subjects.map(subject => (
              <li key={subject.subjectId}>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedSubjects.includes(subject.subjectId)}
                    onChange={() => handleCheckboxChange(subject.subjectId)}
                  />
                  {subject.subjectName}
                </label>
              </li>
            ))}
          </ul>
          <button type="submit" disabled={submitting}>Create Exam</button>
        </form>
      ) : (
        <button onClick={() => setFormOpen(true)}>+Add Exam</button>
      )}
      {showSuccessPopup && <SuccessPopup onClose={closeSuccessPopup} />}

      <div>
        <h2>Exams with Subjects</h2>
        <table>
          <thead>
            <tr>
              <th>Serial no</th>
              <th>Exam Name</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Subjects</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
          {examsWithSubjects.map(exam => (
            <tr key={exam.examId}>
              <td>{exam.examId}</td>
              <td>{exam.examName}</td>
              <td>{formatDate(exam.startDate)}</td>
              <td>{formatDate(exam.endDate)}</td>
              <td>{exam.subjects}</td>
              <td>
              <button onClick={() => handleEdit(exam.examId)}>Edit</button>
                <button onClick={() => handleDelete(exam.examId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
        </table>
      </div>
    </div>
  );
}

export default Examcreation;
