import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Examcreation() {
  const [examName, setExamName] = useState('');
  const [validTime, setValidTime] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [selectedSubjects, setSelectedSubjects] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    // Fetch subjects from the backend when the component mounts
    axios.get('/subjects')
      .then(response => {
        setSubjects(response.data);
      })
      .catch(error => {
        console.error('Error fetching subjects:', error);
      });
  }, []);

  const handleCheckboxChange = (subjectId) => {
    // Toggle the selection of subjects
    setSelectedSubjects((prevSelected) => {
      if (prevSelected.includes(subjectId)) {
        return prevSelected.filter((id) => id !== subjectId);
      } else {
        return [...prevSelected, subjectId];
      }
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitting(true);

    const examData = {
      examName,
      validTime,
      startDate,
      endDate,
      selectedSubjects,
    };

    axios.post('/exams', examData)
      .then(response => {
        console.log('Exam created:', response.data);
        // Reset form fields and state as needed
        setSubmitting(false);
      })
      .catch(error => {
        console.error('Error creating exam:', error);
        setSubmitting(false);
      });
  };

  return (
    <div>
      <h2>Create Exam</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Exam Name:
          <input type="text" value={examName} onChange={(e) => setExamName(e.target.value)} />
        </label>
        <br />
        <label>
          Valid Time:
          <input type="text" value={validTime} onChange={(e) => setValidTime(e.target.value)} />
        </label>
        <br />
        <label>
          Start Date:
          <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        </label>
        <br />
        <label>
          End Date:
          <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
        </label>
        <br />
        <label>Subjects:</label>
        <ul>
          {subjects.map(subject => (
            <li key={subject.subjectId}>
              <label>
                <input
                  type="checkbox"
                  checked={selectedSubjects.includes(subject.subjectId)}
                  onChange={() => handleCheckboxChange(subject.subjectId)}
                />
                {subject.subjectName}
              </label>
            </li>
          ))}
        </ul>
        <button type="submit" disabled={submitting}>Create Exam</button>
      </form>
    </div>
  );
}

export default Examcreation;
