import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EditForm({ exam, onClose, onUpdate }) {
  const [editedExam, setEditedExam] = useState({ ...exam });
  const [editedSubjects, setEditedSubjects] = useState([]);

  useEffect(() => {
    // Fetch subjects for the selected exam if needed
    if (exam) {
      axios
        .get(`http://localhost:3081/exams/${exam.examId}/subjects`)
        .then((response) => {
          const selectedSubjects = response.data.map((subject) => subject.subjectName);
          setEditedSubjects(selectedSubjects);
        })
        .catch((error) => {
          console.error('Error fetching subjects for the selected exam:', error);
        });
    }
  }, [exam]);

  const handleSave = () => {
    // Send a request to update the exam data on the server
    axios
      .put(`http://localhost:3081/exams-edit/${editedExam.examId}`, {
        examName: editedExam.examName,
        startDate: editedExam.startDate,
        endDate: editedExam.endDate,
        subjects: editedSubjects,
      })
      .then((response) => {
        // Notify the parent component that the data has been updated
        onUpdate({ ...editedExam, subjects: editedSubjects });
      })
      .catch((error) => {
        console.error('Error updating exam:', error);
      });
  };

  return (
    <div className="edit-form">
      <h2>Edit Exam</h2>
      <label>
        Exam Name:
        <input
          type="text"
          value={editedExam.examName}
          onChange={(e) => setEditedExam({ ...editedExam, examName: e.target.value })}
        />
      </label>
      <br />
      <label>
        Start Date:
        <input
          type="date"
          value={editedExam.startDate}
          onChange={(e) => setEditedExam({ ...editedExam, startDate: e.target.value })}
        />
      </label>
      <br />
      <label>
        End Date:
        <input
          type="date"
          value={editedExam.endDate}
          onChange={(e) => setEditedExam({ ...editedExam, endDate: e.target.value })}
        />
      </label>
      <br />
      <label>Subjects:</label>
      <ul className="subject-list">
        {editedSubjects.map((subject, index) => (
          <li key={index}>
            <input
              type="text"
              value={subject}
              onChange={(e) => {
                const updatedSubjects = [...editedSubjects];
                updatedSubjects[index] = e.target.value;
                setEditedSubjects(updatedSubjects);
              }}
            />
          </li>
        ))}
      </ul>
      <button onClick={handleSave}>Save</button>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
}

export default EditForm;
