import React, { useState, useEffect } from 'react';

function  Coursecreation() {

  const [typeOfTests, setTypeOfTests] = useState([]);
  const [selectedTypeOfTest, setSelectedTypeOfTest] = useState('');
 const [exams, setExams] = useState([]);
 const [selectedexams, setSelectedexams] = useState('');
 const [selectedSubjects, setSelectedSubjects] = useState([]);
 const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [typeofquestion,setTypeofquetion] = useState([]);
  // // const [selectTypeofquestion,setselectTypeofquestion] = useState('');
  const [selectTypeofquestion, setselectTypeofquestion] = useState([]);
const [loadingTypeOfTests, setLoadingTypeOfTests] = useState(true);
const [loadingQuestionTypes, setLoadingQuestionTypes] = useState(true);
const [loadingExams, setLoadingExams] = useState(true);
 const [formData, setFormData] = useState({
  courseName: '',
  examId: '',
  typeOfTestId: '',
  courseStartDate: '',
  courseEndDate: '',
  cost: '',
  discount: '',
  discountAmount: '',
  totalPrice: '',
  
});

  useEffect(() => {
    fetch('http://localhost:3081/type-of-tests')
      .then(response => response.json())
      .then(data => {
        setTypeOfTests(data);
      })
      .catch(error => console.error('Error fetching type of tests:', error));
  }, []); 

  const handleTypeOfTestChange = (event) => {
    setSelectedTypeOfTest(event.target.value);
  };


  useEffect(() => {
    fetch('http://localhost:3081/quesion_type')
      .then(response => response.json())
      .then(data => {
        setTypeofquetion(data);
      })
      .catch(error => console.error('Error fetching exams:', error));
  }, []);

  useEffect(() => {
    fetch('http://localhost:3081/courese-exams')
      .then(response => response.json())
      .then(data => {
        setExams(data);
      })
      .catch(error => console.error('Error fetching exams:', error));
  }, []);



  const handleexams = async (event) => {
    const selectedExamId = event.target.value;
  
    // Fetch subjects for the selected exam
    try {
      const response = await fetch(`http://localhost:3081/courese-exam-subjects/${selectedExamId}/subjects`);
      const subjectsData = await response.json();
      setSelectedSubjects(subjectsData);
    } catch (error) {
      console.error('Error fetching subjects:', error);
    }
  
    setSelectedexams(selectedExamId);
  };

  const handleSubjectChange = (event, subjectId) => {
    const isChecked = event.target.checked;
  
    if (isChecked) {
      // Add the selected subject to the list
      setSelectedSubjects([...selectedSubjects, subjectId]);
    } else {
      // Remove the deselected subject from the list
      setSelectedSubjects(selectedSubjects.filter(id => id !== subjectId));
    }
  };

  // const handletypeofquestionChange = (event, quesionTypeId) => {
  //   const isChecked = event.target.checked;
  
  //   if (isChecked) {
  //     // Add the selected subject to the list
  //     setselectTypeofquestion([...selectTypeofquestion, quesionTypeId]);
  //   } else {
  //     // Remove the deselected subject from the list
  //     setselectTypeofquestion(selectTypeofquestion.filter(id => id !== quesionTypeId));
  //   }
  // };
  
  useEffect(() => {
    fetch('http://localhost:3081/type-of-tests')
      .then(response => response.json())
      .then(data => {
        setTypeOfTests(data);
        setLoadingTypeOfTests(false);
      })
      .catch(error => {
        console.error('Error fetching type of tests:', error);
        setLoadingTypeOfTests(false);
      });
  }, []); 

  const handleChange = (e) => {
    const { name, value } = e.target;
  
    // Directly parse cost and discount from their input values
    if (name === 'cost' || name === 'discount') {
      const cost = name === 'cost' ? parseFloat(value) : formData.cost;
      const discount = name === 'discount' ? parseFloat(value) : formData.discount;
  
      // Calculate discount amount
      const discountAmount = (!isNaN(cost) && !isNaN(discount)) ? (cost * discount) / 100 : '';
  
      // Calculate total price after discount
      const totalPrice = (!isNaN(cost) && !isNaN(discountAmount)) ? cost - discountAmount : '';
  
      // Update the form data including formatted dates
      setFormData({
        ...formData,
        typeOfTestId: selectedTypeOfTest,
        examId: selectedexams,
        subjects: selectedSubjects,
        courseStartDate: startDate,
        courseEndDate: endDate,
        cost: cost,
        discount: discount,
        discountAmount: discountAmount,
        totalPrice: totalPrice,
      });
    } else if (name === 'courseStartDate' || name === 'courseEndDate') {
      // For date fields, update as usual
      setFormData({ ...formData, [name]: value });
    } else {
      // For other fields, update as usual
      setFormData({ ...formData, [name]: value });
    }
  };


  const handleSubmit = async e => {
    e.preventDefault();

    // Prepare the data for submission
    const data = {
      ...formData,
      typeOfTestId: selectedTypeOfTest,
      examId: selectedexams,
      subjects: selectedSubjects,
    };

    // Submit the data to the server
    try {
      const response = await fetch('http://localhost:3081/course-creation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();
      console.log(result);
      // Handle success or show a success message to the user
    } catch (error) {
      console.error('Error submitting course data:', error);
      // Handle error or show an error message to the user
    }
  };

  const handleStartDateChange = (e) => {
    const formattedDate = e.target.value;
    setStartDate(formattedDate);
  };
  
  const handleEndDateChange = (e) => {
    const formattedDate = e.target.value;
    setEndDate(formattedDate);
  };
  
  return (
    <div>
      <h2>Create a New Course</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="courseName">Course Name:</label>
          <input
            type="text"
            id="courseName"
            name="courseName"
            value={formData.courseName}
            onChange={handleChange}
          />
        </div>

        <div>
      <label htmlFor="typeOfTest">Select Type of Test:</label>
      <select id="typeOfTest" value={selectedTypeOfTest} onChange={handleTypeOfTestChange}>
        <option value="">Select Type of Test</option>
        {typeOfTests.map(test => (
          <option key={test.typeOfTestId} value={test.typeOfTestId}>
            {test.typeOfTestName}
          </option>
        ))}
      </select>
    </div>


    <div>
      <label htmlFor="exams">Select Exam:</label>
      <select id="exams" value={selectedexams} onChange={handleexams}>
        <option value="">Select exams</option>
        {exams.map(exams => (
          <option key={exams.examId} value={exams.examId}>
            {exams.examName}
          </option>
        ))}
      </select>
    </div>

    <div>
  <label>Subjects:</label>
  {selectedSubjects.map(subject => (
    <div key={subject.subjectId}>
      <input
        type="checkbox"
        id={`subject-${subject.subjectId}`}
        name={`subject-${subject.subjectId}`}
        value={subject.subjectId}
        checked={selectedSubjects.includes(subject.subjectId)}
        onChange={(e) => handleSubjectChange(e, subject.subjectId)}
      />
      <label htmlFor={`subject-${subject.subjectId}`}>{subject.subjectName}</label>
    </div>
  ))}
</div>


<div>
        <label htmlFor="typeOfTest">Select Type of Test:</label>
        <select id="typeOfTest" value={selectedTypeOfTest} onChange={handleTypeOfTestChange}>
          {/* ... */}
        </select>
        {loadingTypeOfTests && <p>Loading type of tests...</p>}
      </div>

      {/* Similar adjustments for other select elements */}
      
      <div>
        <label>Type of Question:</label>
        {selectTypeofquestion.map(typeofquestion => (
          <div key={typeofquestion.quesionTypeId}>
            {/* ... */}
          </div>
        ))}
        {loadingQuestionTypes && <p>Loading question types...</p>}
      </div>
<div>
          <label htmlFor="courseStartDate">Start Date:</label>
          <input
            type="date"
            id="courseStartDate"
            name="courseStartDate"
            value={startDate}
            onChange={handleStartDateChange}
          />
        </div>

        <div>
          <label htmlFor="courseEndDate">End Date:</label>
          <input
            type="date"
            id="courseEndDate"
            name="courseEndDate"
            value={endDate}
            onChange={handleEndDateChange}
          />
        </div>
        <div>
          <label htmlFor="cost">Cost:</label>
          <input
            type="number"
            id="cost"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="discount">Discount (%):</label>
          <input
            type="number"
            id="discount"
            name="discount"
            value={formData.discount}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="discountAmount">Discount Amount:</label>
          <input
            type="number"
            id="discountAmount"
            name="discountAmount"
            value={formData.discountAmount}
            readOnly // Make it read-only to prevent manual input
          />
        </div>

        <div>
          <label htmlFor="totalPrice">Total Price:</label>
          <input
            type="number"
            id="totalPrice"
            name="totalPrice"
            value={formData.totalPrice}
            readOnly // Make it read-only to prevent manual input
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Coursecreation;
