import React, { useState } from 'react';

function  Coursecreation() {
  const [formData, setFormData] = useState({
    courseName: '',
    testType: '',
    examName: '',
    subjects: '',
    questionType: '',
    courseStartDate: '',
    courseEndDate: '',
    cost: '',
    discount: '',
    discountAmount: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'cost' || name === 'discount') {
      // If the changed field is 'cost' or 'discount', update discount amount
      const cost = parseFloat(formData.cost);
      const discount = parseFloat(formData.discount);
      const discountAmount = (cost * discount) / 100;
      setFormData({
        ...formData,
        [name]: value,
        discountAmount: isNaN(discountAmount) ? '' : discountAmount,
      });
    } else {
      // For other fields, update as usual
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can handle form submission here
    console.log(formData);
  };

  return (
    <div>
      <h2>Create a New Course</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="courseName">Course Name:</label>
          <input
            type="text"
            id="courseName"
            name="courseName"
            value={formData.courseName}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="testType">Test Type:</label>
          <input
            type="text"
            id="testType"
            name="testType"
            value={formData.testType}
            onChange={handleChange}
          />
        </div>

        {/* ... (other input fields) */}

        <div>
          <label htmlFor="cost">Cost:</label>
          <input
            type="number"
            id="cost"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="discount">Discount (%):</label>
          <input
            type="number"
            id="discount"
            name="discount"
            value={formData.discount}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="discountAmount">Discount Amount:</label>
          <input
            type="number"
            id="discountAmount"
            name="discountAmount"
            value={formData.discountAmount}
            readOnly // Make it read-only to prevent manual input
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Coursecreation;
